import React, { useState, useEffect } from 'react';
import './AppleCatcher.css';

const AppleCatcher = ({ onGameOver }) => {
    const [score, setScore] = useState(0);
    const [time, setTime] = useState(60);
    const [apples, setApples] = useState([]);
    const [playerPosition, setPlayerPosition] = useState(50);
    const [gameStarted, setGameStarted] = useState(false);
    const [scoreAnimations, setScoreAnimations] = useState([]);

    useEffect(() => {
        if (gameStarted && time > 0) {
            const timer = setInterval(() => {
                setTime((prevTime) => prevTime - 1);
            }, 1000);

            return () => clearInterval(timer);
        } else if (time === 0) {
            endGame();
        }
    }, [gameStarted, time]);

    useEffect(() => {
        if (gameStarted) {
            const appleDropper = setInterval(() => {
                setApples((prevApples) => [
                    ...prevApples,
                    {
                        id: Date.now(),
                        x: Math.random() * 100,
                        y: 0,
                        type: Math.random() > 0.2 ? 'good' : 'rotten',
                    },
                ]);
            }, 1000);

            return () => clearInterval(appleDropper);
        }
    }, [gameStarted]);

    useEffect(() => {
        if (gameStarted) {
            const gameLoop = setInterval(() => {
                setApples((prevApples) =>
                    prevApples.map((apple) => ({
                        ...apple,
                        y: apple.y + 1,
                    })).filter((apple) => apple.y < 100)
                );

                checkCollisions();
            }, 50);

            return () => clearInterval(gameLoop);
        }
    }, [gameStarted, playerPosition]);

    const startGame = () => {
        setGameStarted(true);
        setScore(0);
        setTime(60);
        setApples([]);
    };

    const endGame = () => {
        setGameStarted(false);
        onGameOver(score);
    };

    const checkCollisions = () => {
        setApples((prevApples) =>
            prevApples.filter((apple) => {
                if (
                    apple.y > 70 &&
                    apple.y < 85 &&
                    Math.abs(apple.x - playerPosition) < 10
                ) {
                    const scoreChange = apple.type === 'good' ? 10 : -10;
                    setScore((prevScore) => prevScore + scoreChange);
                    
                    const animationId = Date.now();
                    setScoreAnimations(prev => [...prev, {
                        id: animationId,
                        x: apple.x,
                        y: apple.y,
                        score: scoreChange
                    }]);
                    
                    setTimeout(() => {
                        setScoreAnimations(prev => prev.filter(anim => anim.id !== animationId));
                    }, 500);

                    return false;
                }
                return apple.y < 100;
            })
        );
    };

    const movePlayer = (e) => {
        if (gameStarted) {
            const newPosition = (e.clientX / window.innerWidth) * 100;
            setPlayerPosition(newPosition);
        }
    };

    return (
        <div className="apple-catcher" onMouseMove={movePlayer}>
            <div className="game-info">
                <span>Score: {score}</span>
                <span>Time: {time}</span>
            </div>
            {!gameStarted && (
                <button className="start-button" onClick={startGame}>Start Game</button>
            )}
            <div className="game-area">
                {apples.map((apple) => (
                    <div
                        key={apple.id}
                        className={`apple ${apple.type}`}
                        style={{ left: `${apple.x}%`, top: `${apple.y}%` }}
                    />
                ))}
                {scoreAnimations.map((anim) => (
                    <div
                        key={anim.id}
                        className={`score-animation ${anim.score > 0 ? 'positive' : 'negative'}`}
                        style={{ 
                            left: `${anim.x}%`, 
                            top: `${anim.y}%`,
                            opacity: 1,
                            transform: 'translateY(-20px)'
                        }}
                    >
                        {anim.score > 0 ? '+10' : '-10'}
                    </div>
                ))}
                <div
                    className="player"
                    style={{ left: `${playerPosition}%` }}
                />
            </div>
        </div>
    );
};

export default AppleCatcher;
