import React, { useState, useEffect } from 'react';
import './AppleCatcher.css';
import backgroundImage from './img/apple-tree';

const AppleCatcher = ({ onGameOver }) => {
    const [score, setScore] = useState(0);
    const [time, setTime] = useState(60);
    const [apples, setApples] = useState([]);
    const [playerPosition, setPlayerPosition] = useState(50);
    const [gameStarted, setGameStarted] = useState(false);

    useEffect(() => {
        if (gameStarted && time > 0) {
            const timer = setInterval(() => {
                setTime((prevTime) => prevTime - 1);
            }, 1000);

            return () => clearInterval(timer);
        } else if (time === 0) {
            endGame();
        }
    }, [gameStarted, time]);

    useEffect(() => {
        if (gameStarted) {
            const appleDropper = setInterval(() => {
                setApples((prevApples) => [
                    ...prevApples,
                    {
                        id: Date.now(),
                        x: Math.random() * 100,
                        y: 0,
                        type: Math.random() > 0.2 ? 'good' : 'rotten',
                    },
                ]);
            }, 1000);

            return () => clearInterval(appleDropper);
        }
    }, [gameStarted]);

    useEffect(() => {
        if (gameStarted) {
            const gameLoop = setInterval(() => {
                setApples((prevApples) =>
                    prevApples.map((apple) => ({
                        ...apple,
                        y: apple.y + 1,
                    })).filter((apple) => apple.y < 100)
                );

                checkCollisions();
            }, 50);

            return () => clearInterval(gameLoop);
        }
    }, [gameStarted, playerPosition]);

    const startGame = () => {
        setGameStarted(true);
        setScore(0);
        setTime(60);
        setApples([]);
    };

    const endGame = () => {
        setGameStarted(false);
        onGameOver(score);
    };

    const checkCollisions = () => {
        setApples((prevApples) =>
            prevApples.filter((apple) => {
                if (
                    apple.y > 90 &&
                    apple.y < 100 &&
                    Math.abs(apple.x - playerPosition) < 10
                ) {
                    setScore((prevScore) =>
                        prevScore + (apple.type === 'good' ? 10 : -10)
                    );
                    return false;
                }
                return true;
            })
        );
    };

    const movePlayer = (e) => {
        if (gameStarted) {
            const newPosition = (e.clientX / window.innerWidth) * 100;
            setPlayerPosition(newPosition);
        }
    };

    return (
        <div className="apple-catcher" onMouseMove={movePlayer}>
            <div className="game-info">
                <span>Score: {score}</span>
                <span>Time: {time}</span>
            </div>
            {!gameStarted && (
                <button onClick={startGame}>Start Game</button>
            )}
            <div className="game-area">
                {apples.map((apple) => (
                    <div
                        key={apple.id}
                        className={`apple ${apple.type}`}
                        style={{ left: `${apple.x}%`, top: `${apple.y}%` }}
                    />
                ))}
                <div
                    className="player"
                    style={{ left: `${playerPosition}%` }}
                />
            </div>
        </div>
    );
};

export default AppleCatcher;
