import React, { useState, useEffect, useRef } from 'react';
import './AppleCatcher.css';
import goodAppleImg from './img/good-apple.png';
import rottenAppleImg from './img/rotten-apple.png';
import catcherImg from './img/catcher.png';
import backgroundMusic from './audio/background-music.mp3';
import buttonClickSound from './audio/button-click.mp3';

const AppleCatcher = ({ onGameOver }) => {
    const [score, setScore] = useState(0);
    const [time, setTime] = useState(60);
    const [apples, setApples] = useState([]);
    const [playerPosition, setPlayerPosition] = useState(50);
    const [gameStarted, setGameStarted] = useState(false);
    const [showResult, setShowResult] = useState(false);
    const [isMusicPlaying, setIsMusicPlaying] = useState(false);

    const backgroundMusicRef = useRef(new Audio(backgroundMusic));
    const buttonClickSoundRef = useRef(new Audio(buttonClickSound));
    const gameLoopRef = useRef();
    const lastAppleTimeRef = useRef(0);

    useEffect(() => {
        const audio = backgroundMusicRef.current;
        audio.loop = true;

        return () => {
            audio.pause();
            setIsMusicPlaying(false);
        };
    }, []);

    useEffect(() => {
        if (gameStarted && time > 0) {
            const timer = setInterval(() => {
                setTime((prevTime) => prevTime - 1);
            }, 1000);

            return () => clearInterval(timer);
        } else if (time === 0) {
            endGame();
        }
    }, [gameStarted, time]);

    useEffect(() => {
        if (gameStarted) {
            gameLoopRef.current = requestAnimationFrame(gameLoop);
        }
        return () => {
            if (gameLoopRef.current) {
                cancelAnimationFrame(gameLoopRef.current);
            }
        };
    }, [gameStarted]);

    const gameLoop = (timestamp) => {
        if (timestamp - lastAppleTimeRef.current > 1000) {
            addApple();
            lastAppleTimeRef.current = timestamp;
        }

        setApples(prevApples => 
            prevApples.map(apple => ({
                ...apple,
                y: apple.y + 1.4 // Уменьшаем скорость падения на 30%
            })).filter(apple => apple.y < 100)
        );

        checkCollisions();
        gameLoopRef.current = requestAnimationFrame(gameLoop);
    };

    const addApple = () => {
        if (apples.length < 10) {
            setApples(prevApples => [
                ...prevApples,
                {
                    id: Date.now(),
                    x: Math.random() * 100,
                    y: 0,
                    type: Math.random() > 0.2 ? 'good' : 'rotten',
                }
            ]);
        }
    };

    const playButtonClickSound = () => {
        buttonClickSoundRef.current.currentTime = 0;
        buttonClickSoundRef.current.play().catch(error => console.error("Error playing button sound:", error));
    };

    const startGame = () => {
        playButtonClickSound();
        setGameStarted(true);
        setScore(0);
        setTime(60);
        setApples([]);
        setShowResult(false);
        
        if (!isMusicPlaying) {
            backgroundMusicRef.current.play()
                .then(() => setIsMusicPlaying(true))
                .catch(error => console.error("Error playing background music:", error));
        }
    };

    const endGame = () => {
        setGameStarted(false);
        setShowResult(true);
        backgroundMusicRef.current.pause();
        setIsMusicPlaying(false);
    };

    const checkCollisions = () => {
        setApples(prevApples => 
            prevApples.filter(apple => {
                if (
                    apple.y > 70 &&
                    apple.y < 85 &&
                    Math.abs(apple.x - playerPosition) < 15
                ) {
                    const scoreChange = apple.type === 'good' ? 10 : -10;
                    setScore(prevScore => prevScore + scoreChange);
                    return false;
                }
                return true;
            })
        );
    };

    const movePlayer = (e) => {
        if (gameStarted) {
            const newPosition = (e.clientX / window.innerWidth) * 100;
            setPlayerPosition(newPosition);
        }
    };

    const handleResultClose = () => {
        playButtonClickSound();
        setShowResult(false);
        onGameOver(score);
    };

    return (
        <div className="apple-catcher" onMouseMove={movePlayer}>
            <div className="game-info">
                <span>Score: {score}</span>
                <span>Time: {time}</span>
            </div>
            {!gameStarted && !showResult && (
                <button className="start-button" onClick={startGame}>Start Game</button>
            )}
            <div className="game-area">
                {apples.map((apple) => (
                    <img
                        key={apple.id}
                        src={apple.type === 'good' ? goodAppleImg : rottenAppleImg}
                        alt={apple.type === 'good' ? 'Good Apple' : 'Rotten Apple'}
                        className={`apple ${apple.type}`}
                        style={{ 
                            left: `${apple.x}%`,
                            top: `${apple.y}%`,
                            width: '30px',
                            height: 'auto'
                        }}
                    />
                ))}
                <img
                    src={catcherImg}
                    alt="Catcher"
                    className="player"
                    style={{ 
                        left: `${playerPosition}%`,
                        width: '90px',
                        height: '90px'
                    }}
                />
            </div>
            {showResult && (
                <div className="result-modal">
                    <div className="result-content">
                        <h2>Game Over!</h2>
                        <p>Your score: {score}</p>
                        <button onClick={handleResultClose}>OK</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AppleCatcher;
